sys_variable_value.value:
(function execute(inputs, outputs) {

  var base_prompt = "You are ChatGPT, a large language model trained by OpenAI. You answer as concisely as possible for each response (e.g. Don't be verbose).\\n";
  if( inputs.history_json != "" ) {
   		var jsonObject = JSON.parse(inputs.history_json);
    	var arr = [];
    	for( var i=0; i < jsonObject.length; i++ ) {
			var val = jsonObject[i];
        	arr.push(val.toString());
        }
    	var chat_history = arr;
  }
  else {
    	var chat_history = inputs.history;
  }

  var chatprompt = base_prompt + chat_history.join("\\n") + "\\nUser: " + inputs.prompt + "\\nChatGPT: ";

  while( chatprompt.length > 4000 * 4 ) {
    chat_history.shift();
    chatprompt = base_prompt + chat_history.join("\\n") + "\\nUser: " + inputs.prompt + "\\nChatGPT: ";
  }

outputs.chat = chatprompt + "\\n\\n\\n";
outputs.history = chat_history;

})(inputs, outputs);

