sys_variable_value.value:
(function execute(inputs, outputs) {

  var parsed = JSON.parse(inputs.json);
  var outtext = "";
  var history = inputs.history;

  try {
    if (!completion.choices) {
		outtext = "NULL";
    }
    if (completion.choices.length === 0) {
		outtext = "NULL";
    }
    if (!completion.choices[0].text) {
      outtext = "NULL";
    }
  } catch(e) {}

  if( outtext == "NULL" )
    outputs.response = "";
  else {
    var outstring = parsed.choices[0].text;
    outstring = outstring.replace("<|im_end|>","");
    outputs.response = outstring;
    if( history.length != 0 ) {
	    history.push("User: " + inputs.prompt); 
	    history.push("ChatGPT: " + outstring);
    }
  }

  outputs.history = history;
  outputs.history_json = JSON.stringify(history);

})(inputs, outputs);

