sys_variable_value.value:
(function execute(inputs, outputs) {

  var base_prompt = "You are ChatGPT, a large language model trained by OpenAI. You answer as concisely as possible for each response (e.g. Don't be verbose).";
  if( inputs.history_json != "" ) {
   		var jsonObject = JSON.parse(inputs.history_json);
    	var arr = [];
    	for( var i=0; i < jsonObject.length; i++ ) {
			var val = jsonObject[i];
        	arr.push(val.toString());
        }
    	var chat_history = arr;
  }
  else {
    	var chat_history = inputs.history;
  }

  var chatprompt = base_prompt + chat_history.join("\\n") + "\\nUser: " + inputs.prompt + "\\nChatGPT: ";

  while( chatprompt.length > 4000 * 4 ) {
    chat_history.shift();
    chatprompt = chatprompt + chat_history.join("\\n") + "\\nUser: " + inputs.prompt + "\\nChatGPT: ";
  }

  /* New API conversation format */

  chatprompt = '{\"role\": \"system\", \"content\": \"' + base_prompt + '\"}';

  for( var j=0; j < chat_history.length; j++ ) {

    var tmpstr = chat_history[j];

    if( tmpstr.startsWith('User: ') ) {
      chatprompt = chatprompt + ', {\"role\": \"user\", \"content\": \"' + tmpstr.substring(6).replace(/"/g, '\\"').replace(/\\\\/g, '\\').replace(/\n/g, ' ')  + '\"}';
    }

    if( tmpstr.startsWith('ChatGPT: ') ) {
      chatprompt = chatprompt + ', {\"role\": \"assistant\", \"content\": \"' + tmpstr.substring(9).replace(/"/g, '\\"').replace(/\\\\/g, '\\').replace(/\n/g, ' ')  + '\"}';
    }

  }
  if ( inputs.prompt.trim() != "" ) {
	  chatprompt = chatprompt + ', {\"role\": \"user\", \"content\": \"' + inputs.prompt + '\"}';
  }

  //var chatprompt = base_prompt + chat_history.join("\\n") + "\\nUser: " + inputs.prompt + "\\nChatGPT: ";



outputs.chat = chatprompt;
outputs.history = chat_history;

})(inputs, outputs);

