Build string with pre-training prompt from pre-training table table. 
*/

var context = fd_data.flow_var.pre_training_context;
var preT = new GlideRecord('x_snc_askgpt_chatgpt_pretraining');

var pre_training_gpt_prompt = '';

preT.addQuery('context', context); 
preT.orderBy('updated');
preT.query();

while( preT.next() ) {
 pre_training_gpt_prompt = pre_training_gpt_prompt + 'Caller: ' + preT.getValue('prompt').replace(/"/g, '\\"').replace(/\\\\/g, '\\') + '\\n';
 pre_training_gpt_prompt = pre_training_gpt_prompt + 'Agent: ' + preT.getValue('completion').replace(/"/g, '\\"').replace(/\\\\/g, '\\') + '\\n';
}

return pre_training_gpt_prompt;","savedValue":""}}

